// Add Header.jsx, HoLeftBar.jsx here
// CarList included in Home.jsx
// Hide Vin for each car's info

// up to date - wxm
import React, { useEffect, useState } from 'react';
import axios from 'axios';
// import HoHeader from './HoHeader';
// import HomeLeftBar from './HoLeftBar';
// import './Home.css';

function HomeLeftBar({ applyTextSearch, applySelectionFilters, cars }) {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchType, setSearchType] = useState('all'); 
  const [selectedMake, setSelectedMake] = useState('');
  const [selectedExColor, setSelectedExColor] = useState('');
  const [selectedInColor, setSelectedInColor] = useState('');
  const [selectedFuel, setSelectedFuel] = useState('');
  const [selectedYear, setSelectedYear] = useState('');
  const [selectedMileageRange, setSelectedMileageRange] = useState('');

  const makes = Array.from(new Set(cars.map(car => car.make)));
  const exColors = Array.from(new Set(cars.map(car => car.exterior_color)));
  const inColors = Array.from(new Set(cars.map(car => car.interior_color)));
  const fuels = Array.from(new Set(cars.map(car => car.fuel)));
  const years = Array.from(new Set(cars.map(car => car.year)));
  const mileageRanges = [
      { label: "0 - 10,000", value: "0-10000" },
      { label: "10,001 - 20,000", value: "10001-20000" },
      { label: "20,001 - 30,000", value: "20001-30000" },
      // Add more ranges as needed
  ];

  const handleTextSearch = (e) => {
      e.preventDefault(); // 阻止表单默认提交事件
      if (!searchTerm.trim()) { // 检查搜索词是否为空或只包含空格
          alert("Please enter a search term."); // 可以用更友好的方式显示这个信息
          return; // 退出函数，不执行搜索
      }
      applyTextSearch(searchTerm); // 执行搜索
  };


  const handleSelectionSearch = (e) => {
      e.preventDefault();
      applySelectionFilters({
          make: selectedMake,
          exteriorColor: selectedExColor,
          interiorColor: selectedInColor,
          fuel: selectedFuel,
          year: selectedYear,
          mileageRange: selectedMileageRange,
          // ... pass other selected attributes
      });
  };

  return (
      <div className="filter-bar">
          {/* Text Search Form */}
          <form onSubmit={handleTextSearch}>
              <input
                  type="text"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  placeholder="Search By Make!"
              />
              <button type="submit">Search</button>
          </form>

          {/* Dropdown Selections Form */}
          <form onSubmit={handleSelectionSearch}>
              <select value={selectedMake} onChange={(e) => setSelectedMake(e.target.value)}>
                  <option value="">Make</option>
                  {makes.map(make => <option key={make} value={make}>{make}</option>)}
              </select>

              <select value={selectedExColor} onChange={(e) => setSelectedExColor(e.target.value)}>
                  <option value="">Exterior Color</option>
                  {exColors.map(color => <option key={color} value={color}>{color}</option>)}
              </select>

              <select value={selectedInColor} onChange={(e) => setSelectedInColor(e.target.value)}>
                  <option value="">Interior Color</option>
                  {inColors.map(color => <option key={color} value={color}>{color}</option>)}
              </select>

              <select value={selectedFuel} onChange={(e) => setSelectedFuel(e.target.value)}>
                  <option value="">Fuel Type</option>
                  {fuels.map(fuel => <option key={fuel} value={fuel}>{fuel}</option>)}
              </select>

              <select value={selectedYear} onChange={(e) => setSelectedYear(e.target.value)}>
                  <option value="">Year</option>
                  {years.map(year => <option key={year} value={year}>{year}</option>)}
              </select>

              <select value={selectedMileageRange} onChange={(e) => setSelectedMileageRange(e.target.value)}>
                  <option value="">Mileage Range</option>
                  {mileageRanges.map(range => (
                      <option key={range.value} value={range.value}>{range.label}</option>
                  ))}
              </select>
              <button type="submit">Search</button>
          </form>
      </div>
  );
}

function Cars() {
    const [cars, setCars] = useState([]);
    const [filteredCars, setFilteredCars] = useState([]);
    const [hasSearched, setHasSearched] = useState(false);

    useEffect(() => {
        const fetchCars = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/cars`);
                setCars(response.data);
            } catch (error) {
                console.error('Error fetching cars:', error);
            }
        };

        fetchCars();
    }, []);

    const applyTextSearch = (searchTerm) => {
        setHasSearched(true);
        const lowercasedTerm = searchTerm.toLowerCase();
        const filtered = cars.filter(car =>
            car.make.toLowerCase() === lowercasedTerm
        );
        setFilteredCars(filtered);
    };

    const applySelectionFilters = (filters) => {
        setHasSearched(true);
        const filtered = cars.filter(car => 
            (!filters.make || car.make === filters.make) &&
            (!filters.exteriorColor || car.exterior_color === filters.exteriorColor) &&
            (!filters.interiorColor || car.interior_color === filters.interiorColor) &&
            (!filters.fuel || car.fuel === filters.fuel) &&
            (!filters.year || car.year.toString() === filters.year) &&
            (!filters.mileageRange || (function() {
                const [min, max] = filters.mileageRange.split('-').map(Number);
                return car.current_mileage >= min && car.current_mileage <= max;
            })())
        );
        setFilteredCars(filtered);
    };

    const displayCars = hasSearched ? filteredCars : cars;

    return (
      <div className="cars-page-container">
        {/* <HoHeader />
        <div className="cars-content">
          <HomeLeftBar 
            applyTextSearch={applyTextSearch} 
            applySelectionFilters={applySelectionFilters} 
            cars={cars} 
          /> */}
            {displayCars.map((car, index) => (
              <div className="car-card" key={index}>
                {/* Display the car image */}
                <img className="car-image" src={car.image} alt={`${car.make} ${car.model}`} />
                {/* Display car details */}
                <div className="car-details">
                  <h3>{car.make} {car.model}</h3>
                  <p>Exterior Color: {car.exterior_color}</p>
                  <p>Interior Color: {car.interior_color}</p>
                  <p>Fuel Type: {car.fuel}</p>
                  <p>Year: {car.year}</p>
                  {/* Other details can be included here, but we'll omit the VIN */}
                  <p>Mileage: {car.current_mileage} miles</p>
                  <p>Price: ${car.list_price}</p>
                  {/* If there are more details you want to display, add them here */}
                </div>
              </div>
            ))}
        </div>
    //   </div>
    );


}

const HoHeader = () => {
  return (
      <header className="home-header">
          <img className='logo' src={Logo} alt="Car Bidding Logo" />
          <div className="header-title">
              <h1>CarBiddingSystem</h1>
          </div>

          {/* <div className="group-info">
              <p>Group Members: Anning Tian, Pingyi Xu, QinHao Zhang, Xinmeng Wu</p>
              <p>Class: CS5200 Database Management Systems</p>
              <p>Professor: Dr. Tehmina Amjad</p>
          </div> */}
          <br></br>
          <nav className="app-nav">
              <Link to="/" className="nav-link"><button className="btn">Home</button></Link>
              <Link to="/login" className="nav-link"><button className="btn">Login</button></Link>
              <Link to="/create-account" className="nav-link"><button className="btn">Create Account</button></Link>
              <Link to="/admin-login" className="nav-link"><button className="btn">Admin Login</button></Link>
          </nav>
      </header>
  );
};

export default Cars;
