import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';



function LoginPage() {
  const [email, setEmail] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const navigate = useNavigate();


  const handleLogin = async (event) => {
    event.preventDefault();
    console.log('Login with:', { email, phone_number: phoneNumber });
    try {
        const response = await axios.post(`${process.env.REACT_APP_API_URL}login/`, {
            email,
            phone_number: phoneNumber
        });
        console.log(response.data)

        if (response.data && response.data.user) {
          localStorage.setItem('userRole', response.data.user.isSeller ? 'seller' : 'bidder');
          // Store the email in localStorage
          localStorage.setItem('userEmail', email);
        } else {
          console.error('Unexpected response format:', response.data);
        }

        // Redirect to UserPage after successful login
        navigate('/user');  // Adjust the path as needed
    } catch (error) {
        if (error.response) {
            console.error('Error logging in:', error.response.data.message);
        } else {
            console.error('Error logging in:', error.message);
        }
    }
};


  return(
<div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleLogin} className="login-form">
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
        />
        <input
          type="tel"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
          placeholder="Phone Number"
          required
        />
        <button type="submit">Login</button>
      </form>
    </div>
  )
}

export default LoginPage;
