// Home.jsx

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
// import './HoHeader.css'; 
// import './HoLeftBar.css'; 
// import './Home.css';
import Logo from './CarBidding.png';

function Home() {
    const [cars, setCars] = useState([]);
    const [filteredCars, setFilteredCars] = useState([]);
    const [hasSearched, setHasSearched] = useState(false);
    const [searchTerm, setSearchTerm] = useState('');
    const [searchType, setSearchType] = useState('all'); 
    const [selectedMake, setSelectedMake] = useState('');
    const [selectedExColor, setSelectedExColor] = useState('');
    const [selectedInColor, setSelectedInColor] = useState('');
    const [selectedFuel, setSelectedFuel] = useState('');
    const [selectedYear, setSelectedYear] = useState('');
    const [selectedMileageRange, setSelectedMileageRange] = useState('');

    useEffect(() => {
        const fetchCars = async () => {
            try {
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/cars`);
                setCars(response.data);
            } catch (error) {
                console.error('Error fetching cars:', error);
            }
        };

        fetchCars();
    }, []);

    const applyTextSearch = (e) => {
        e.preventDefault(); 
        setHasSearched(true);
        const lowercasedTerm = searchTerm.toLowerCase();
        const filtered = cars.filter(car =>
            car.make.toLowerCase() === lowercasedTerm
        );
        setFilteredCars(filtered);
    };

    const applySelectionFilters = (e) => {
        e.preventDefault();
        setHasSearched(true);
        const filtered = cars.filter(car => 
            (!selectedMake || car.make === selectedMake) &&
            (!selectedExColor || car.exterior_color === selectedExColor) &&
            (!selectedInColor || car.interior_color === selectedInColor) &&
            (!selectedFuel || car.fuel === selectedFuel) &&
            (!selectedYear || car.year.toString() === selectedYear) &&
            (!selectedMileageRange || (function() {
                const [min, max] = selectedMileageRange.split('-').map(Number);
                return car.current_mileage >= min && car.current_mileage <= max;
            })())
        );
        setFilteredCars(filtered);
    };

    const displayCars = hasSearched ? filteredCars : cars;

    return (
        <div className="home-container">
            <header className="home-header">
                <img className='logo' src={Logo} alt="Car Bidding Logo" />
                <div className="header-title">
                    <h1>CarBiddingSystem</h1>
                </div>
                <nav className="app-nav">
                    <Link to="/" className="nav-link"><button className="btn">Home</button></Link>
                    <Link to="/login" className="nav-link"><button className="btn">Login</button></Link>
                    <Link to="/create-account" className="nav-link"><button className="btn">Create Account</button></Link>
                    <Link to="/admin-login" className="nav-link"><button className="btn">Admin Login</button></Link>
                </nav>
            </header>
            <div className="cars-content">
                <div className="filter-bar">
                    <form onSubmit={applyTextSearch}>
                        <input
                            type="text"
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            placeholder="Search By Make!"
                        />
                        <button type="submit">Search</button>
                    </form>
                    <form onSubmit={applySelectionFilters}>
                        {/* Add your dropdowns here */}
                        <button type="submit">Search</button>
                    </form>
                </div>
                {displayCars.map((car, index) => (
                    <div className="car-card" key={index}>
                        {/* Display the car image */}
                        <img className="car-image" src={car.image} alt={`${car.make} ${car.model}`} />
                        {/* Display car details */}
                        <div className="car-details">
                            <h3>{car.make} {car.model}</h3>
                            <p>Exterior Color: {car.exterior_color}</p>
                            <p>Interior Color: {car.interior_color}</p>
                            <p>Fuel Type: {car.fuel}</p>
                            <p>Year: {car.year}</p>
                            {/* Other details can be included here, but we'll omit the VIN */}
                            <p>Mileage: {car.current_mileage} miles</p>
                            <p>Price: ${car.list_price}</p>
                            {/* If there are more details you want to display, add them here */}
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Home;
